<template>    <div>        <v-subheader>{{ Label }}</v-subheader>        <quill v-model="Content" :config="Config"></quill>    </div></template><script>import "katex/dist/katex.js";import 'katex/dist/katex.min.css';import 'quill/dist/quill.bubble.css';export default {    name: 'EditorView',    data() {        return {            Content: this.content === null || this.content === undefined ? {opt: {}} : this.content,            Label: this.label,        }    },    computed: {        Config() {            if (this.readOnly) {                return {                    readOnly: true,                    theme: 'bubble',                };            }            return {                modules: {                    formula: true,                    toolbar: [                        ["bold", "italic", "underline", "strike"],                        [{list: "ordered"}, {list: "bullet"}],                        [{color: []}, {background: []}],                        [{align: []}],                        ["image"],                        ["formula"]                    ]                },            };        },    },    props: {        content: {type: [Object, String], default: null,},        label: {type: String, default: '',},        readOnly: {type: Boolean, default: false,},    },    methods: {        onInput() {            if (this.readOnly) return;            this.$emit('input', this.Content);        },    },    components: {},    watch: {        Content(n) {            this.$emit('input', n);        }    }}</script>